import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

const RestaurantDetail = () => {
  const { parentId } = useParams();
  const [restaurantList, setRestaurantList] = useState([]);
  // /api/restaurants/${restaurantId}cancelIdleCallback
  useEffect(() => {
    const fetchRestaurantData = async () => {
      try {
        const response = await fetch(`http://localhost:8001/api/addrestaurant/${parentId}?populate=lists`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        console.log(parentId);
        if (response.ok) {
          const data = await response.json();
          console.log(data);
          setRestaurantList(data);
        } else {
          console.error('Error:', response.status, response.statusText);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchRestaurantData();
  }, [parentId]); // Include parentId in the dependency array to re-run the effect when parentId changes

  return (
    <div>
      <div className="container mt-4">
        <h1 className="mt-4">Restaurant List</h1>
        
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {restaurantList.map((restaurant) => (
            <div key={restaurant._id} className="col">
              <Link to={`/display/${restaurant.parentId}`} className="text-decoration-none">
                <div className="card h-100 bg-danger text-white">
                  <div className="card-body">
                  <h3>Items:</h3>
          <ul>
            {restaurant.lists.map((item) => (
              <li key={item._id}>{item.itemName}</li>
            ))}
          </ul>
                    <h1 className="card-title">{restaurant.restaurantName}</h1>
                    <p className="card-text">{restaurant.areaName}</p>
                    <p className="card-text">{restaurant.Rating} - {restaurant.availability ? "Open" : "Closed"}</p>
                    <p className="card-text">{restaurant.DeliveryTime} mins - {restaurant.cuisines}</p>
                  </div>
                </div>
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RestaurantDetail;
