// RestaurantDetail.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const RestaurantDetail = () => {
  const [restaurant, setRestaurant] = useState(null);
  const { restaurantName } = useParams();

  useEffect(() => {
    const fetchRestaurantDetail = async () => {
      try {
        const response = await fetch(`http://localhost:8001/api/addrestaurant`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        
        if (response.ok) {
          const data = await response.json();
          console.log(data);
          setRestaurant(data);
        } else {
          console.error('Error:', response.status, response.statusText);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchRestaurantDetail();
  }, [restaurantName]);

  if (!restaurant) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mt-4">
        {"hello"}
      <h1 className="mt-4">{restaurant.restaurantName}</h1>
      <div className="card bg-danger text-white">
        <div className="card-body">
          <p className="card-text">{restaurant.areaName}</p>
          <p className="card-text">{restaurant.Rating} - {restaurant.availability ? "Open" : "Closed"}</p>
          <p className="card-text">{restaurant.DeliveryTime} mins - {restaurant.cuisines}</p>
          {/* Add more details as needed */}
        </div>
      </div>
    </div>
  );
};

export default RestaurantDetail;
