import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

export default function Signup() {
  const navigate = useNavigate();

  const [credentials, setCredentials] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    geolocation: '',
  });

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setCredentials({ ...credentials, [name]: value });

    if (name === 'password') {
      checkPasswordStrength(value);
    }

    if (name === 'phone') {
      checkMobileNumber(value);
    }
  };

  const checkPasswordStrength = (password) => {
    const passwordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%^&+=]).{8,}$/;
    setCredentials((prevCredentials) => ({
      ...prevCredentials,
      passwordStrength: passwordRegex.test(password) ? 'strong' : 'weak',
      passwordMessage: passwordRegex.test(password)
        ? 'Strong password'
        : 'Weak password (minimum 8 characters, including uppercase, lowercase, numbers, and special characters)',
    }));
  };

  const checkMobileNumber = (phone) => {
    const phoneRegex = /^\d{10}$/;
    setCredentials((prevCredentials) => ({
      ...prevCredentials,
      phoneError: phoneRegex.test(phone) ? '' : 'Mobile number must be 10 digits',
    }));
  };

  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };

  const handleToggleConfirmPassword = () => {
    setShowConfirmPassword(!showConfirmPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if there are any errors before submitting
    if (credentials.phoneError) {
      alert('Please correct the form errors before submitting.');
      return;
    }

    const response = await fetch('http://localhost:8001/api/auth/createuser', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(credentials),
    });

    const json = await response.json();

    if (json.success) {
      localStorage.setItem('user_credentials', JSON.stringify(credentials));
      navigate('/auth/login');
    }
  };

  return (
    <>
      <div div className="container d-flex text-align-center justify-content-center">
        <div className="m-2 text-light col-8 display-flex align-items-center justify-content-center ">
          <h1 className="text-center fw-4 text-secondary pt-5 text-uppercase p-4 fs-2">
            Signup Page:
          </h1>

          <form className="custom-form mb-4" onSubmit={handleSubmit}>
            <div className="container border-radius-20 p-5">
              <div className="row mb-4 align-items-center ">
                <label className="col-form-label col-4 fw-bold">Name:</label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control border-radius-10"
                    name="name"
                    value={credentials.name}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="row mb-3 align-items-center ">
                <label className="col-form-label col-4 fw-bold">Email:</label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control border-radius-10"
                    name="email"
                    value={credentials.email}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="row mb-3 align-items-center ">
                <label className="col-form-label col-4 fw-bold">Password:</label>
                <div className="col-8">
                  <div className="input-group">
                    <input
                      type={showPassword ? 'text' : 'password'}
                      className="form-control border-radius-10"
                      name="password"
                      value={credentials.password}
                      onChange={handleChange}
                      required
                    />
                    <button
                      className="btn btn-outline-secondary"
                      type="button"
                      onClick={handleTogglePassword}
                      style={{ height: '38px' }}
                    >
                      {showPassword ? <i class="fa-solid fa-eye-slash"></i> : <i class="fa-solid fa-eye"></i>}
                    </button>
                  </div>
                </div>
              </div>

              <div className="row  align-items-center ">
                <label className="col-form-label col-4 fw-bold">Confirm Password:</label>
                <div className="col-8">
                  <div className="input-group">
                    <input
                      type={showConfirmPassword ? 'text' : 'password'}
                      className="form-control border-radius-10"
                      name="confirmPassword"
                      value={credentials.confirmPassword}
                      onChange={handleChange}
                      required
                    />
                    <button
                      className="btn btn-outline-secondary mb-3"
                      type="button"
                      onClick={handleToggleConfirmPassword}
                      style={{ height: '38px' }}
                    >
                      {showConfirmPassword ? <i class="fa-solid fa-eye-slash"></i> : <i class="fa-solid fa-eye"></i>}
                    </button>
                  </div>
                </div>
              </div>

              <div className="row mb-3 align-items-center ">
                <label className="col-form-label col-4 fw-bold">Phone:</label>
                <div className="col-8">
                  <input
                    type="text"
                    className={`form-control border-radius-10 ${
                      credentials.phoneError ? 'is-invalid' : ''
                    }`}
                    name="phone"
                    value={credentials.phone}
                    onChange={handleChange}
                    required
                  />
                  {credentials.phoneError && (
                    <div className="invalid-feedback">{credentials.phoneError}</div>
                  )}
                </div>
              </div>

              <div className="row mb-3 align-items-center ">
                <label className="col-form-label col-4 fw-bold">Geolocation:</label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control border-radius-10"
                    name="geolocation"
                    value={credentials.geolocation}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="text-center">
                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
                <Link to="/auth/login" className="btn btn-danger ms-2">
                  Go to Login
                </Link>
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  );
}
