import React, { useState, useEffect } from 'react';

const Review = () => {
  const authToken = localStorage.getItem('authToken');
  const userEmail = localStorage.getItem('userEmail');
  console.log(userEmail);

  const [parentOptions, setParentOptions] = useState([]);
  const [formData, setFormData] = useState({
    message: '',
    rating: 0,
    parentName:'',
    email:userEmail,
    name:userName,
  });

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await fetch('http://localhost:8001/api/auth/addrestaurant');
        const data = await response.json();
        const restaurantNames = data.map((restaurant) => restaurant.restaurantName);
        setParentOptions(restaurantNames);
      } catch (error) {
        console.error('Error fetching restaurant names:', error.message);
      }
    };

    fetchRestaurants();
  }, []);


  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://localhost:8001/api/userdata', {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
        });

        if (response.ok) {
          const users = await response.json();
          const filteredUser = users.find((user) => user.email === useremail);

          if (filteredUser) {
            setUserData(filteredUser);
            setUpdatedUser(filteredUser);
          } else {
            console.error("User with email not found");
          }
        } else {
          console.error("Failed to fetch user data");
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    }

    fetchData();
  });


  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8001/api/review', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log(response);

      if (!response.ok) {
        console.error(`Request failed with status ${response.status}`);
        return;
      }

      const json = await response.json();
      console.log('API Response:', json);

      // Adjust the properties based on your server response
      setFormData({
        message: '',
        rating: 0,
      });

      alert('Form filled successfully');
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        Message:
        <input
          type="text"
          placeholder="message"
          name="message"
          value={formData.message}
          onChange={handleChange}
        />

        Rating:
        <input
          type="number"
          placeholder="Rating"
          name="rating"
          value={formData.rating}
          onChange={handleChange}
        />

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Review;
