import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

const RestaurantDetail = () => {
  const { _id } = useParams();
  const [restaurant, setRestaurant] = useState(null);

  useEffect(() => {
    const fetchRestaurantData = async () => {
      try {
        const response = await fetch(`http://localhost:8001/api/addRestaurant/${_id}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          const dataArray = Array.isArray(data) ? data : [data];
          setRestaurant(dataArray[0]);
        } else {
          console.error('Error:', response.status, response.statusText);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchRestaurantData();
  }, [_id]);

  return (
    <div className="container mt-4">
      <h1 className="mt-4">Restaurant Detail</h1>
      
      {restaurant && (
        <>
          <h2>{restaurant.restaurantName}</h2>
          <p>{restaurant.areaName}</p>
          <p><strong>Rating:</strong> {restaurant.Rating} - {restaurant.areaName}</p>
          <p><strong>Availability:</strong> {restaurant.availability ? 'Open' : 'Closed'}</p>
          <p><strong>Cuisines:</strong> {restaurant.cuisines.join(', ')}</p>

          <h3>Lists:</h3>
          <div className="card p-2 m-2 " style={{ backgroundColor: '#F5F5F5', padding: '10px', margin: '10px 0' }}>
            {restaurant.lists.map((list) => (
              <div key={list._id} style={{ marginBottom: '15px' }}>
                <img src={list.cloudinaryImageId} alt={list.itemName} style={{ maxWidth: '100%' }} />
                <p style={{ marginTop: '10px' }}><strong>Name:</strong> {list.itemName}</p>
                <p><strong>Category:</strong> {list.category}</p>
                <p><strong>Rating:</strong> {list.Rating}</p>
                <p><strong>Availability:</strong> {list.availability ? 'Available' : 'Not Available'}</p>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default RestaurantDetail;
