// AddItemForm.jsx
import React, { useState } from 'react';
import './AddItemForm.css'; // Import the custom CSS file for additional styling

const AddItemForm = () => {
  // State to hold form data
  const [formData, setFormData] = useState({
    itemName: 'Pasta Alfredo',
    cloudinaryImageId: 'image3',
    category: 'Pasta',
    parentName: 'Italian Delight',
    Rating: 5,
    cost: 1,
    availability: true,
    description: 'optional',
  });

  // Function to handle form input changes
  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);

    try {
      const response = await fetch('http://localhost:8001/api/createItem', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log(response);

      if (!response.ok) {
        console.error(`Request failed with status ${response.status}`);
        // Handle error here, e.g., show an error message to the user
        return;
      }

      const json = await response.json();
      console.log('API Response:', json);

      // Add logic for handling the API response, e.g., show a success message

      // Reset the form after submission
      setFormData({
        itemName: '',
        cloudinaryImageId: '',
        category: '',
        parentName: '',
        Rating: '',
        cost: '',
        availability: '',
        description: '',
      });
    } catch (error) {
      console.error('Error:', error.message);
      // Handle other errors here
    }
  };

  return (
    <div className="container mt-5">
      <div className="card p-4">
        <h2 className="mb-4">Add a New Item</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="itemName" className="form-label">
              Item Name:
            </label>
            <input
              type="text"
              name="itemName"
              value={formData.itemName}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          {/* Add other form fields similarly */}

          <button type="submit" className="btn btn-primary">
            Add Item
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddItemForm;
