import React, { useState, useEffect } from 'react';

const Review = () => {
  const userEmail = localStorage.getItem('userEmail');
  const [userName, setUserName] = useState('Akash');
  const [parentOptions, setParentOptions] = useState([]);
  const [formData, setFormData] = useState({
    Message: '',
    Rating: 0,
    parentName: '', 
    email: userEmail,
    name: userName,
  });

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://localhost:8001/api/userdata', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const users = await response.json();
          const filteredUser = users.find((user) => user.email === userEmail);

          if (filteredUser) {
            console.log(filteredUser);
            console.log(filteredUser.name);
            setUserName(filteredUser.name);
          } else {
            console.error('User with email not found');
          }
        } else {
          console.error('Failed to fetch user data');
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    }

    fetchData();
  });
  console.log(formData);

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await fetch('http://localhost:8001/api/auth/addrestaurant');
        const data = await response.json();
        const restaurantNames = data.map((restaurant) => restaurant.restaurantName);
        setParentOptions(restaurantNames);
      } catch (error) {
        console.error('Error fetching restaurant names:', error.message);
      }
    };

    fetchRestaurants();
  }, []);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8001/api/review', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      console.log(response);
      console.log(formData);
      if (!response.ok) {
        console.error(`Request failed with status ${response.status}`);
        return;
      }
      const json = await response.json();
      console.log('API Response:', json);

      setFormData({
        Message: '',
        Rating: 0,
        parentName:'',
        email: userEmail,
        name: userName,
      });

      alert('Form filled successfully');
    } catch (error) {
      console.error('Error:', error.message);
    }
  };
  return (<>
    
      <form onSubmit={handleSubmit}>
        <div className="conainer display-flex justify-content-center text-align-center">
          <div> Message:
        <input
          type="text"
          placeholder="Message"
          name="Message"
          value={formData.Message}
          onChange={handleChange}
        /></div>
       <div> Rating:
        <input
          type="number"
          placeholder="Rating"
          name="Rating"
          value={formData.Rating}
          onChange={handleChange}
        /></div>
       
        <div className="row mb-4 align-items-center">
          <label htmlFor="parentName" className="col-form-label col-4 fw-bold">
            Parent Name (Restaurant):
          </label>
          <div className="col-8">
            <select
              name="parentName"
              value={formData.parentName} 
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="" disabled>
                Select a restaurant
              </option>
              {parentOptions.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
            </select>
          </div>
        </div>

        <button type="submit">Submit</button>
        </div>
       
      </form>
    
  
  </>
    
  );
};

export default Review;
