import React, { useState, useEffect } from 'react';
import './additem.css';

const AddItemForm = () => {
  const [formData, setFormData] = useState({
    itemName: 'Pasta Alfredo',
    cloudinaryImageId: 'image3',
    category: 'Pasta',
    parentName: '',
    Rating: 5,
    cost: 1,
    availability: true,
    description: 'optional',
  });

  const [parentOptions, setParentOptions] = useState([]);

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await fetch('http://localhost:8001/api/auth/addrestaurant');
        const data = await response.json();
        const restaurantNames = data.map((restaurant) => restaurant.restaurantName);
        setParentOptions(restaurantNames);
      } catch (error) {
        console.error('Error fetching restaurant names:', error.message);
      }
    };

    fetchRestaurants();
  }, []);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);

    try {
      const response = await fetch('http://localhost:8001/api/auth/createItem', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log(response);

      if (!response.ok) {
        console.error(`Request failed with status ${response.status}`);
        return;
      }

      const json = await response.json();
      console.log('API Response:', json);

      setFormData({
        itemName: '',
        cloudinaryImageId: '',
        category: '',
        parentName: '',
        Rating: '',
        cost: '',
        availability: '',
        description: '',
      });

      alert('Form filled successfully');
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  const { itemName, cloudinaryImageId, category, parentName, Rating, cost, availability, description } = formData;

  return (
    <>
     <div className="contianer d-flex justify-content-center m-4">
      <div className="">
        <h2 className="mb-4 text-center text-primary">Add a New Item</h2>
        <form className="custom-form" onSubmit={handleSubmit}>
          <div className="container border-radius-20 p-5">
            <div className="row mb-4 align-items-center">
              <label className="col-form-label col-4 fw-bold">ItemName:</label>
              <div className="col-8">
                <input
                  type="text"
                  className="form-control border-radius-10"
                  name="itemName"
                  value={itemName}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="row mb-4 align-items-center">
              <label className="col-form-label col-4 fw-bold">Image:</label>
              <div className="col-8">
                <input
                  type="text"
                  className="form-control border-radius-10"
                  name="cloudinaryImageId"
                  value={cloudinaryImageId}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="row mb-4 align-items-center">
              <label className="col-form-label col-4 fw-bold">Cost:</label>
              <div className="col-8">
                <input
                  type="text"
                  className="form-control border-radius-10"
                  name="cost"
                  value={cost}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>

            <div className="row mb-4 align-items-center">
              <label htmlFor="description" className="col-form-label col-4 fw-bold">Description:</label>
              <div className="col-8">
                <input
                  type="text"
                  name="description"
                  value={description}
                  onChange={handleChange}
                  className="form-control"
                  required
                />
              </div>
            </div>

            <div className="row mb-4 align-items-center">
              <label htmlFor="category" className="col-form-label col-4 fw-bold">Category:</label>
              <div className="col-8">
                <input
                  type="text"
                  name="category"
                  value={category}
                  onChange={handleChange}
                  className="form-control"
                  required
                />
              </div>
            </div>

            <div className="row mb-4 align-items-center">
              <label htmlFor="parentName" className="col-form-label col-4 fw-bold">Parent Name (Restaurant):</label>
              <div className="col-8">
                <select
                  name="parentName"
                  value={parentName}
                  onChange={handleChange}
                  className="form-select"
                  required
                >
                  <option value="" disabled>Select a restaurant</option>
                  {parentOptions.map((option) => (
                    <option key={option} value={option}>{option}</option>
                  ))}
                </select>
              </div>
            </div>

            <div className="row mb-4 align-items-center">
              <label className="col-form-label col-4 fw-bold">Rating:</label>
              <div className="col-8">
                <input
                  type="number"
                  name="Rating"
                  value={Rating}
                  onChange={handleChange}
                  className="form-control"
                  required
                />
              </div>
            </div>

            <div className="row mb-4 align-items-center">
              <label className="col-form-label col-4 fw-bold">Availability:</label>
              <div className="col-8 d-flex justify-content-space between">
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="availability"
                    id="availabilityTrue"
                    value="true"
                    checked={availability === true}
                    onChange={handleChange}
                  />
                  <label className="form-check-label" htmlFor="availabilityTrue">True</label>
                </div>
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="availability"
                    id="availabilityFalse"
                    value="false"
                    checked={availability === false}
                    onChange={handleChange}
                  />
                  <label className="form-check-label" htmlFor="availabilityFalse">False</label>
                </div>
              </div>
            </div>

            <div className="text-center">
              <button type="submit" className="btn btn-primary">
                Add Item
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
    </>
   
  );
};

export default AddItemForm;
