import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
// ... (any other imports)

const Display = () => {
  const [restaurantList, setRestaurantList] = useState([]);

  useEffect(() => {
    const fetchRestaurantData = async () => {
      try {
        const response = await fetch('http://localhost:8001/api/addrestaurant', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          setRestaurantList(data);
        } else {
          console.error('Error:', response.status, response.statusText);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchRestaurantData();
  }, []); // Empty dependency array to run the effect only once when the component mounts

  return (
    <div>
      <div className="container mt-4">
        <h1 className="mt-4">Restaurant List</h1>
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {restaurantList.map((restaurant) => (
            <div key={restaurant._id} className="col">
              <div className="card h-100 bg-danger text-white">
                <div className="card-body">
                  <h1 className="card-title">{restaurant.restaurantName}</h1>
                  <p className="card-text">{restaurant.areaName}</p>
                  <p className="card-text">{restaurant.Rating} - {restaurant.availability ? "Open" : "Closed"}</p>
                  <p className="card-text">{restaurant.DeliveryTime} mins - {restaurant.cuisines}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Display;
