import React, { useState } from 'react';

const AddRestaurantForm = () => {
  const [formData, setFormData] = useState({
    restaurantName: "Alpha", 
    areaName: "lucknow",
    Rating: 4,
    DeliveryTime: 45,
    availability: 'true',
    cloudinaryImageId:'12',
    parentId:'',
  });

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);

    try {
      const response = await fetch('http://localhost:8001/api/addRestaurant', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log(response);

      if (!response.ok) {
        console.error(`Request failed with status ${response.status}`);
        return;
      }

      const json = await response.json();
      console.log('API Response:', json);

      setFormData({
        restaurantName: '',
        areaName: '',
        Rating: '',
        DeliveryTime: '',
        availability: '',
        cloudinaryImageId:'',
        parentId:'',
      });
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-8">
          <h2 className='text-center fw-2 text-secondary mb-4'>Add a New Restaurant:</h2>
          <form className="custom-form" onSubmit={handleSubmit}>
            {/* ... (your existing form inputs) */}
            <div className="mb-3">
              <label className="form-label fw-bold mt-4">Availability:</label>
              <div className="form-check form-check-inline">
                {/* ... (your radio buttons for availability) */}
              </div>
            </div>
            <div className="text-center">
              <button type="submit" className="btn btn-primary">Add Restaurant</button>
            </div>
          </form>
        </div>
        <div className="col-md-4">
          <img
            src={`https://res.cloudinary.com/your-cloud-name/image/upload/${formData.cloudinaryImageId}`}
            alt="Cloudinary"
            className="img-fluid"
          />
        </div>
      </div>
    </div>
  );
};

export default AddRestaurantForm;
