import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Badge from 'react-bootstrap/Badge';
import Modal from 'react-bootstrap/Modal';
import Cart from '../screens/Cart';
import { useCart } from './ContextReducer';
import { LOG_URL } from '../constants';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
// import {useAuth} from 

const Navbar = () => {
  const [cartView, setCartView] = useState(false);
  const data = useCart();
  const navigate = useNavigate();

  const authToken = localStorage.getItem('authToken');
  const userEmail = localStorage.getItem('userEmail');

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/auth/login'); // Redirect to the login page after logout
  };
  
  const loadCart = () => {
    setCartView(true);
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark fs-5 " style={{ backgroundColor: 'rgba(0, 0, 0, 1.0)' }}>
        <div className="container-fluid">
          <Link className="navbar-brand border bg-white rounded-circle" to="/">
            <img
              src={LOG_URL}
              alt="Fast Food"
              style={{
                maxWidth: '50px',
                height: 'auto',
                marginRight: '10px',
                marginTop: '2px',
              }}
            />
          </Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto mb-2 mx-2 px-3">
              <li className="nav-item px-3">
                <Link className="nav-link " aria-current="page" to="/" key="home" activeClassName="active-link">
                  Home
                </Link>
              </li>
              <li className="nav-item active" >
                {authToken ? (
                  <Link className="nav-link px-3" aria-current="page" to="/myorder" key="myorders">
                    My Orders
                  </Link>
                ) : null}
              </li>
              <li className="nav-item">
                <Link className="nav-link px-3" aria-current="page" to="/menu" key="menu">
                  Menu
                </Link>
              </li>
              <li className="nav-item px-2">
                <Link className="nav-link" aria-current="page" to="/about" key="about">
                  About
                </Link>
              </li>
              <li className="nav-item px-3 dropdown">
                {authToken ? (
                  <>
                    <Link className="nav-link navbar-dark dropdown-toggle" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false" key="user-dropdown">
                      <FontAwesomeIcon icon={faUser} className="me-1"/> 
                      {userEmail}
                    </Link>
                    <div className="dropdown-menu dropdown-menu-dark" aria-labelledby="navbarDropdown">
                      <Link className="dropdown-item" to="/dashboard/profile" key="profile">
                        Profile
                      </Link>
                      <Link className="dropdown-item" to="/myorder" key="orders">
                        Orders
                      </Link>
                      <div className="dropdown-divider"></div>
                    </div>
                  </>
                ) : (
                  <>
                    <Link className="nav-link dropdown-toggle" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false" key="guest-dropdown">
                      <FontAwesomeIcon icon={faUser} className="me-1" />
                       User
                    </Link>
                    <div className="dropdown-menu dropdown-menu-dark" aria-labelledby="navbarDropdown">
                      <Link className="dropdown-item" to="/auth/login" key="login">
                        Login
                      </Link>
                      <Link className="dropdown-item" to="/auth/createuser" key="signup">
                        Signup
                      </Link>
                    </div>
                  </>
                )}
              </li>
              <li className="nav-item px-3">
                <Link className="nav-link" aria-current="page" to="/contact" key="contact">
                  Contact us
                </Link>
              </li>
            </ul>

            <div className="">
              {authToken ? (
                <button className="btn bg-white text-success mx-1" onClick={handleLogout} key="logout">
                  Logout
                </button>
              ) : (
                <>
                  <Link className="btn bg-danger text-white mx-1" to="/auth/login" key="login-btn">
                    Login
                  </Link>
                  <Link className="btn bg-danger text-white mx-1" to="/auth/createuser" key="signup-btn">
                    Signup
                  </Link>
                </>
              )}
            </div>
            <div className="d-flex align-items-center fs-1">
              <button className="btn btn mx-1 bg-danger text-light mx-1" onClick={loadCart} key="cart-btn">
                <FontAwesomeIcon icon={faShoppingCart} className="me-1" />Cart
                {data.length !== 0 && (
                  <Badge pill bg="danger">
                    {data.length}
                  </Badge>
                )}
              </button>
            </div>
          </div>
        </div>
      </nav>
      {cartView && (
        <Modal show={cartView} onHide={() => setCartView(false)} dialogClassName="custom-modal-dialog modal-lg">
          <Modal.Header closeButton className="bg-warning">
            <Modal.Title className="text-danger">My Cart</Modal.Title>
          </Modal.Header>
          <Modal.Body className="bg-light">
            <Cart />
          </Modal.Body>
          <Modal.Footer className="bg-light">
            <button type="button" className="btn btn-primary">
              Save changes
            </button>
          </Modal.Footer>
        </Modal>
      )}
    </>
  );
};

export default Navbar;
