// ... (your import statements)

const RestaurantMenu = () => {
  const [resInfo, setResInfo] = useState(null);
  const { resId } = useParams();
  const [isImageZoomed, setImageZoomed] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [selectedItems, setSelectedItems] = useState({}); // To store selected items for each category

  useEffect(() => {
    const fetchDataAndSetResInfo = async () => {
      try {
        const data = await fetch(MENU_API + resId);
        const json = await data.json();
        console.log(json);
        setResInfo(json.data);
      } catch (error) {
        console.error("Error fetching menu data:", error);
      }
    };

    fetchDataAndSetResInfo();
  }, [resId]);

  const handleCardClick = (index) => {
    setSelectedCard(index);
    setSelectedItems({}); // Reset selected items when a new card is clicked
  };

  const handleDropdownChange = (categoryId, itemId) => {
    setSelectedItems((prevSelectedItems) => ({
      ...prevSelectedItems,
      [categoryId]: itemId,
    }));
  };

  return (
    <div style={{ minHeight: "100vh", background: "rgba(255, 255, 0, 0.1)" }}>
      <div className="container">
        {/* ... (your existing code) */}

        <h1 className="menu-header mt-4 fs-1 px-4">MENU</h1>

        {allItems?.cards.slice(1, allItems.cards.length - 2).map((cardList, index) => (
          <div key={index} className="card" onClick={() => handleCardClick(index)}>
            <h2 className="fw-bold fs-3 text-primary">{cardList?.card?.card?.title}</h2>
            {selectedCard === index && (
              <div className="card-content">
                <h2 className="fw-bold fs-3 text-primary">{cardList?.card?.card?.title}</h2>
                <div className="row">
                  {cardList?.card?.card?.categories ? (
                    cardList?.card?.card?.categories.map((category) => (
                      <div key={category?.card?.id} className="mt-4">
                        <h3 className="fw-bold fs-4">{category?.card?.card?.title}</h3>
                        <div className="row">
                          {/* Add a dropdown for each category */}
                          <select
                            id={`dropdown_${category?.card?.id}`}
                            onChange={(e) => handleDropdownChange(category?.card?.id, e.target.value)}
                          >
                            <option value="" disabled selected>
                              Select an item
                            </option>
                            {category?.itemCards.map((item) => (
                              <option key={item?.card?.info?.id} value={item?.card?.info?.id}>
                                {item?.card?.info?.name}
                              </option>
                            ))}
                          </select>
                        </div>
                        {/* Render food items */}
                        <div className="row">
                          {category?.itemCards.map((item) => (
                            <div
                              className="col-lg-4 col-md-6 col-sm-12 mb-3"
                              key={item?.card?.info?.id}
                            >
                              <div>
                                <FoodDataCard
                                  foodItem={item?.card?.info}
                                  url={`${IMAGE_URL}${item?.card?.info?.imageId}`}
                                />
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    ))
                  ) : (
                    // Render food items for categories without subcategories
                    category?.itemCards.map((item) => (
                      <div className="col-lg-4 col-md-6  mb-4 col-sm-12" key={item?.card?.info?.id}>
                        <div>
                          <FoodDataCard
                            foodItem={item?.card?.info}
                            url={`${IMAGE_URL}${item?.card?.info?.imageId}`}
                          />
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </div>
            )}
          </div>
        ))}

        {/* ... (your existing code) */}
      </div>
    </div>
  );
};

export default RestaurantMenu;
