import React, { useState } from 'react';

const AddRestaurantForm = () => {
  const initialState = {
    restaurantName: "Alpha",
    areaName: "Lucknow",
    Rating: 4,
    DeliveryTime: 45,
    availability: 'true',
    cloudinaryImageId: '12',
    parentId: '',
  };

  const [formData, setFormData] = useState(initialState);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);

    try {
      const response = await fetch('http://localhost:8001/api/auth/addRestaurant', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log(response);

      if (!response.ok) {
        console.error(`Request failed with status ${response.status}`);
        return;
      }

      const json = await response.json();
      console.log('API Response:', json);

      setFormData(initialState);
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  return (
    <>
      <div className="row">
        <div className="m-2 card text-light col-8 display-flex align-items-center justify-content-center">
          <h2 className="text-center fw-4 text-secondary pt-5 text-uppercase p-4">
            Create a Restaurant
          </h2>

          <form className="custom-form mb-4" onSubmit={handleSubmit}>
            <div className="container border-radius-20 p-5">
              <div className="row mb-4 align-items-center">
                {renderInput('Name:', 'restaurantName')}
              </div>

              <div className="row mb-3 align-items-center">
                {renderInput('Area Name:', 'areaName')}
              </div>

              <div className="row mb-3 align-items-center">
                {renderInput('Image:', 'cloudinaryImageId')}
              </div>

              <div className="row mb-3 align-items-center">
                {renderInput('Parent Id:', 'parentId')}
              </div>

              <div className="row mb-3 align-items-center">
                {renderInput('Rating:', 'Rating')}
              </div>

              <div className="row mb-3 align-items-center">
                {renderInput('Delivery Time:', 'DeliveryTime')}
              </div>

              <div className="mb-3">
                <label className="form-label fw-bold mt-4">Availability:</label>
                <div className="form-check form-check-inline">
                  {renderRadioInput('True', 'availabilityTrue')}
                </div>
                <div className="form-check form-check-inline">
                  {renderRadioInput('False', 'availabilityFalse')}
                </div>
              </div>

              <div className="text-center">
                <button type="submit" className="btn btn-primary">
                  Add Restaurant
                </button>
              </div>
            </div>
          </form>
        </div>

        <div className="col-3 d-flex">
          <img
            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQBK2ByZJcWy5_thhhnLUX8Xqauf7WrYaGGcQ&usqp=CAU"
            alt=""
          />
        </div>
      </div>
    </>
  );

  function renderInput(label, name) {
    return (
      <>
        <label className="col-form-label col-4 fw-bold">{label}</label>
        <div className="col-8">
          <input
            type="text"
            className="form-control border-radius-10"
            name={name}
            value={formData[name]}
            onChange={handleChange}
            required
          />
        </div>
      </>
    );
  }

  function renderRadioInput(label, id) {
    return (
      <>
        <input
          className="form-check-input"
          type="radio"
          name="availability"
          id={id}
          value={label.toLowerCase()}
          checked={formData.availability === label.toLowerCase()}
          onChange={handleChange}
        />
        <label className="form-check-label" htmlFor={id}>
          {label}
        </label>
      </>
    );
  }
};

export default AddRestaurantForm;
