import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
// ... (any other imports)

const Display = () => {
  const [restaurantList, setRestaurantList] = useState([]);

  useEffect(() => {
    const fetchRestaurantData = async () => {
      try {
        const response = await fetch('http://localhost:8001/api/addrestaurant', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          setRestaurantList(data);
        } else {
          console.error('Error:', response.status, response.statusText);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchRestaurantData();
  }, []); // Empty dependency array to run the effect only once when the component mounts

  return (
    <div>
      <div className="container mt-4">
        <h1 className="mt-4">Restaurant List</h1>
        <ul className="list-unstyled">
          {restaurantList.map((restaurant) => (
            <li key={restaurant._id}>
              <div className="card bg-danger text-white">
                <h1 className="card-title">{restaurant.restaurantName} - {restaurant.areaName}</h1>
                <p className="card-text">{restaurant.Rating} - {restaurant.availability ? "true" : "false"}</p>
                <p>{restaurant.DeliveryTime}-{restaurant.cuisines}
                </p>
              </div>
            </li>
            // Add more details you want to display for each restaurant
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Display;
