import React, { useState, useEffect } from 'react';

const Review = () => {
  const authToken = localStorage.getItem('authToken');
  const userEmail = localStorage.getItem('userEmail');

  const [parentOptions, setParentOptions] = useState([]);
  const [formData, setFormData] = useState({
    message: '',
    rating: 0,
  });

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await fetch('http://localhost:8001/api/auth/addrestaurant');
        const data = await response.json();
        const restaurantNames = data.map((restaurant) => restaurant.restaurantName);
        setParentOptions(restaurantNames);
      } catch (error) {
        console.error('Error fetching restaurant names:', error.message);
      }
    };

    fetchRestaurants();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData); // You can process the form data here
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        Message:
        <input
          type="text"
          placeholder="message"
          name="message"
          value={formData.message}
          onChange={handleChange}
        />

        Rating:
        <input
          type="number"
          placeholder="Rating"
          name="rating"
          value={formData.rating}
          onChange={handleChange}
        />

        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Review;
