import React, { useState } from 'react';

const AddRestaurantForm = () => {
  // State to hold form data
  const [formData, setFormData] = useState({
    id: "22",
    name: "Alpha",
    areaName: "lucknow",
    cost: 330,
    cuisines: "burger",
    rating: 4,
    deliveryTime: 45,
    availability: true,
  });

  // Function to handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);
    try {
      const response = await fetch('/api/addrestaurant', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: (formData), 
      });
  
      if (!response.ok) {
        throw new Error(`Request failed with status ${response.status}`);
      }
  
      const json = await response.json();
      console.log('API Response:', json);
  
      // Add any logic you need to handle the API response
  
      // Reset the form after submission
      setFormData({
        name: '',
        areaName: '',
        cost: '',
        cuisines: '',
        rating: '',
        DeliveryTime: '',
        availability: '',
      });
    } catch (error) {
      console.error('Error:', error.message);
    }
  };
  
  

  return (
    <div>
      <h2>Add a New Restaurant</h2>
      <form onSubmit={handleSubmit}>
        {/* <label>
          ID:
          <input
            type="text"
            name="id"
            value={formData.id}
            onChange={handleChange}
            required
          />
        </label> */}
        <br />

        <label>
          Restaurant Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Area Name:
          <input
            type="text"
            name="areaName"
            value={formData.areaName}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Cost:
          <input
            type="text"
            name="cost"
            value={formData.cost}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Cuisines:
          <input
            type="text"
            name="cuisines"
            value={formData.cuisines}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Rating:
          <input
            type="number"
            name="rating"
            value={formData.rating}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
    Delivery Time:
    <input
        type="text"
        name="DelieveryTime"  // Note the uppercase 'D' and 'T'
        value={formData.DelieveryTime}
        onChange={handleChange}
        required
    />
</label>


        <label>
          Availability:
          <input
            type="text"
            name="availability"
            value={formData.availability}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <button type="submit">Add Restaurant</button>
      </form>
    </div>
  );
};

export default AddRestaurantForm;
