import React, { useState } from 'react';
// {
//     "restaurantName": "Sam",
//     "areaName": "Sam",
//     "cost": 20,
//     "cuisines": "Sample Cuisine",
//     "Rating": 4.5,
//     "DelieveryTime": 30,
//     "availability": true,
//     "_id": "657615115481d4921dbfa705",
//     "createdAt": "2023-12-10T19:44:17.933Z",
//     "updatedAt": "2023-12-10T19:44:17.933Z",
//     "__v": 0
//   }
const AddRestaurantForm = () => {
  // State to hold form data
  const [formData, setFormData] = useState({
    restaurantName: "Alpha", 
    areaName: "lucknow",
    cost: 330,
    cuisines: "burger",
    Rating: 4,
    DeliveryTime: 45,
    availability: true,
  });

  // Function to handle form input changes
  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('/api/addrestaurant', {
      method: 'POST',
     
      body: JSON.stringify(formData),
    });

    const json = await response.json();
    console.log(json);

    
  };
  
  return (
    <div>
      <h2>Add a New Restaurant</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Restaurant Name:
          <input
            type="text"
            name="restaurantName"
            value={formData.restaurantName}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Area Name:
          <input
            type="text"
            name="areaName"
            value={formData.areaName}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Cost:
          <input
            type="number"
            name="cost"
            value={formData.cost}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Cuisines:
          <input
            type="text"
            name="cuisines"
            value={formData.cuisines}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Rating:
          <input
            type="number"
            name="Rating"
            value={formData.Rating}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Delivery Time:
          <input
            type="text"
            name="DeliveryTime" // Update to match the case in the state
            value={formData.DeliveryTime}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Availability:
          <input
            type="text"
            name="availability"
            value={formData.availability}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <button type="submit">Add Restaurant</button>
      </form>
    </div>
  );
};

export default AddRestaurantForm;
