import React, { useState } from 'react';

const AddRestaurantForm = () => {
  // State to hold form data
  const [formData, setFormData] = useState({
    id: '',
    name: '',
    areaName: '',
    cost: '',
    cuisines: '',
    rating: '',
    deliveryTime: '',
    availability: '',
  });

  // Function to handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic to send the form data to your backend or perform any other actions
    console.log('Form Data Submitted:', formData);
    // Reset the form after submission
    setFormData({
      id: '',
      name: '',
      areaName: '',
      cost: '',
      cuisines: '',
      rating: '',
      deliveryTime: '',
      availability: '',
    });
  };

  return (
    <div>
      <h2>Add a New Restaurant</h2>
      <form onSubmit={handleSubmit}>
        <label>
          ID:
          <input
            type="text"
            name="id"
            value={formData.id}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Restaurant Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Area Name:
          <input
            type="text"
            name="areaName"
            value={formData.areaName}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Cost:
          <input
            type="text"
            name="cost"
            value={formData.cost}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Cuisines:
          <input
            type="text"
            name="cuisines"
            value={formData.cuisines}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Rating:
          <input
            type="number"
            name="rating"
            value={formData.rating}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Delivery Time:
          <input
            type="text"
            name="deliveryTime"
            value={formData.deliveryTime}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Availability:
          <input
            type="text"
            name="availability"
            value={formData.availability}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <button type="submit">Add Restaurant</button>
      </form>
    </div>
  );
};

export default AddRestaurantForm;
