import React, { useState } from 'react';
// "name": "Sample1Restaurant",
// "areaName": "Sample1 Area",
// "cost": "20",
// "cuisines": "Sample Cuisine",
// "rating": "4.5",
// "DelieveryTime": "30",
// "availability": true
const AddRestaurantForm = () => {
  // State to hold form data
  const [formData, setFormData] = useState({
    name: "Alpha", 
    areaName: "lucknow",
    cost: 330,
    cuisines: "burger",
    rating: 4,
    DeliveryTime: 45,
    availability: true,
  });

  // Function to handle form input changes
  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('/api/addrestaurant', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });

    const json = await response.json();
    console.log(json);

    
  };
  
  return (
    <div>
      <h2>Add a New Restaurant</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Restaurant Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Area Name:
          <input
            type="text"
            name="areaName"
            value={formData.areaName}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Cost:
          <input
            type="number"
            name="cost"
            value={formData.cost}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Cuisines:
          <input
            type="text"
            name="cuisines"
            value={formData.cuisines}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Rating:
          <input
            type="number"
            name="rating"
            value={formData.rating}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Delivery Time:
          <input
            type="text"
            name="DeliveryTime" // Update to match the case in the state
            value={formData.DeliveryTime}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <label>
          Availability:
          <input
            type="text"
            name="availability"
            value={formData.availability}
            onChange={handleChange}
            required
          />
        </label>
        <br />

        <button type="submit">Add Restaurant</button>
      </form>
    </div>
  );
};

export default AddRestaurantForm;
