import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import Badge from 'react-bootstrap/Badge';
import Modal from 'react-bootstrap/Modal';
import Cart from '../screens/Cart';
import { useCart } from './ContextReducer';
import { LOGO_URL } from '../constants';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart } from '@fortawesome/free-solid-svg-icons';

const Navbar = () => {
  const [cartView, setCartView] = useState(false);
  const data = useCart();
  const navigate = useNavigate();
  const location = useLocation();

  const authToken = localStorage.getItem('authToken');
  const userEmail = localStorage.getItem('userEmail');

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/auth/login');
  };

  const loadCart = () => {
    setCartView(true);
  };

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark fs-5">
        <div className="container">
          <Link className="navbar-brand" to="/">
            <img
              src={LOGO_URL}
              alt="Fast Food"
              style={{
                maxWidth: '50px',
                height: 'auto',
                marginRight: '10px',
              }}
            />
          </Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className={`nav-link ${location.pathname === '/' ? 'active' : ''}`} to="/">
                  Home
                </Link>
              </li>
              {authToken && (
                <li className="nav-item dropdown">
                  <Link
                    className={`nav-link dropdown-toggle ${location.pathname.startsWith('/dashboard') ? 'active' : ''}`}
                    id="userDropdown"
                    role="button"
                    data-bs-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                  >
                    <FontAwesomeIcon icon={faUser} className="me-1" />
                    {userEmail}
                  </Link>
                  <div className="dropdown-menu" aria-labelledby="userDropdown">
                    <Link
                      className={`dropdown-item ${location.pathname === '/dashboard/profile' ? 'active' : ''}`}
                      to="/dashboard/profile"
                    >
                      Profile
                    </Link>
                    <Link
                      className={`dropdown-item ${location.pathname === '/myorder' ? 'active' : ''}`}
                      to="/myorder"
                    >
                      Orders
                    </Link>
                  </div>
                </li>
              )}
              <li className="nav-item">
                <Link className={`nav-link ${location.pathname === '/menu' ? 'active' : ''}`} to="/menu">
                  Menu
                </Link>
              </li>
              <li className="nav-item">
                <Link className={`nav-link ${location.pathname === '/about' ? 'active' : ''}`} to="/about">
                  About
                </Link>
              </li>
              {!authToken && (
                <li className="nav-item dropdown">
                  <Link
                    className={`nav-link dropdown-toggle ${location.pathname.startsWith('/auth') ? 'active' : ''}`}
                    id="guestDropdown"
                    role="button"
                    data-bs-toggle="dropdown"
                    aria-haspopup="true"
                    aria-expanded="false"
                  >
                    <FontAwesomeIcon icon={faUser} className="me-1" />
                    User
                  </Link>
                  <div className="dropdown-menu" aria-labelledby="guestDropdown">
                    <Link
                      className={`dropdown-item ${location.pathname === '/auth/login' ? 'active' : ''}`}
                      to="/auth/login"
                    >
                      Login
                    </Link>
                    <Link
                      className={`dropdown-item ${location.pathname === '/auth/createuser' ? 'active' : ''}`}
                      to="/auth/createuser"
                    >
                      Signup
                    </Link>
                  </div>
                </li>
              )}
              <li className="nav-item">
                <Link className={`nav-link ${location.pathname === '/contact' ? 'active' : ''}`} to="/contact">
                  Contact us
                </Link>
              </li>
            </ul>

            <div className="d-flex align-items-center fs-1">
              {authToken ? (
                <button className="btn bg-white text-success mx-1" onClick={handleLogout}>
                  Logout
                </button>
              ) : (
                <>
                  <Link className={`btn bg-danger text-white mx-1 ${location.pathname === '/auth/login' ? 'active' : ''}`} to="/auth/login">
                    Login
                  </Link>
                  <Link className={`btn bg-danger text-white mx-1 ${location.pathname === '/auth/createuser' ? 'active' : ''}`} to="/auth/createuser">
                    Signup
                  </Link>
                </>
              )}
              <button className="btn btn mx-1 bg-danger text-light mx-1" onClick={loadCart}>
                <FontAwesomeIcon icon={faShoppingCart} className="me-1" />
                Cart
                {data.length !== 0 && (
                  <Badge pill bg="danger">
                    {data.length}
                  </Badge>
                )}
              </button>
            </div>
          </div>
        </div>
      </nav>
      {cartView && (
        <Modal show={cartView} onHide={() => setCartView(false)} dialogClassName="custom-modal-dialog modal-lg">
          <Modal.Header closeButton className="bg-warning">
            <Modal.Title className="text-danger">My Cart</Modal.Title>
          </Modal.Header>
          <Modal.Body className="bg-light">
            <Cart />
          </Modal.Body>
          <Modal.Footer className="bg-light">
            <button type="button" className="btn btn-primary">
              Save changes
            </button>
          </Modal.Footer>
        </Modal>
      )}
    </>
  );
};

export default Navbar;
