import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({ name: '', email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleTogglePassword = () => {
    setShowPassword(!showPassword);
  };

  const handleToggleConfirmPassword = () => {
    setShowConfirmPassword(!showConfirmPassword);
  };

  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:8001/api/auth/loginuser', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: credentials.name, email: credentials.email, password: credentials.password }),
      });

      const json = await response.json();
      console.log(json);

      if (!json.success) {
        setError('Invalid credentials');
      } else {
        localStorage.setItem('userEmail', credentials.email);
        localStorage.setItem('authToken', json.authToken);
        localStorage.setItem('userdetails', JSON.stringify(json));
        console.log(JSON.stringify(json));

        navigate('/');
      }
    } catch (err) {
      console.error('An error occurred:', err);
      setError('An error occurred while logging in');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (event) => {
    setCredentials({ ...credentials, [event.target.name]: event.target.value });
  };

  return (
    <>
      <div className="container d-flex text-align-center justify-content-center">
        <div className="m-2 text-light col-8 display-flex align-items-center justify-content-center ">
          <h1 className="text-center fw-4 text-secondary pt-5 text-uppercase p-4  fs-2">
            Login Form
          </h1>

          <form className="custom-form mb-4" onSubmit={handleSubmit}>
            <div className="container border-radius-20 p-5">
              

              <div className="row mb-3 align-items-center ">
                <label className="col-form-label col-4 fw-bold">
                  Email:
                </label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control border-radius-10"
                    name="email"
                    value={credentials.email}
                    onChange={handleChange}
                    placeholder='email'
                    required
                  />
                </div>
              </div>

              <div className="row mb-3 align-items-center ">
                <label className="col-form-label col-4 fw-bold">Password:</label>
                <div className="col-8">
                  <div className="input-group">
                    <input
                      type={showPassword ? 'text' : 'password'}
                      className="form-control border-radius-10"
                      name="password"
                      value={credentials.password}
                      onChange={handleChange}
                      required
                    />
                    <button
                      className="btn btn-outline-secondary"
                      type="button"
                      onClick={handleTogglePassword}
                      style={{ height: '38px' }}
                    >
                      {showPassword ? <i class="fa-solid fa-eye-slash"></i> : <i class="fa-solid fa-eye"></i>}
                    </button>
                  </div>
                </div>
              </div>
              <div className="text-center">
                <button type="submit" className="btn btn-primary">
                  Submit
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default Login;
