import React, { useState } from 'react';


const hoverStyles = {
  ':hover': {
    color: 'yellow',
    transform: 'scale(1.2)',
  },
};
const AddRestaurantForm = () => {
  const [formData, setFormData] = useState({
    restaurantName: "Alpha", 
    areaName: "lucknow",
    Rating: 4,
    DeliveryTime: 45,
    availability: 'true',
    cloudinaryImageId:'12',
    parentId:'',
  });

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);

    try {
      const response = await fetch('http://localhost:8001/api/auth/addRestaurant', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log(response);

      if (!response.ok) {
        console.error(`Request failed with status ${response.status}`);
        return;
      }

      const json = await response.json();
      console.log('API Response:', json);

      setFormData({
        restaurantName: '',
        areaName: '',
        Rating: '',
        DeliveryTime: '',
        availability: '',
        cloudinaryImageId:'',
        parentId:'',
      });
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  return (
    <>
      <div className="container">
        <div className="row ">
          <div className="m-2 card bg-dark text-light col-7  display-flex align-items-center justify-content-center ">
            <h2 className="text-center fw-4 text-secondary pt-5 text-uppercase p-4 ">
              Create a Restaurant
            </h2>
            
            <form className="custom-form mb-4" onSubmit={handleSubmit}>
            <div className="container bg-secondary border-radius-20  p-5">
              <div className="row mb-4 align-items-center ">
                <label className="col-form-label col-4 fw-bold">
                Name:
                </label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control bg-light border-radius-10"
                    name="restaurantName"
                    value={formData.restaurantName}
                    onChange={handleChange}
                    required
                    style={hoverStyles[':hover']}
                  />
                </div>
              </div>

              <div className="row mb-3  align-items-center ">
                <label className="col-form-label col-4 fw-bold">
                  Areaname:
                </label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control bg-light border-radius-10"
                    name="areaName"
                    value={formData.areaName}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="row mb-3  align-items-center ">
                <label className="col-form-label col-4 fw-bold">
                  Image:
                </label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control bg-light border-radius-10"
                    name="cloudinaryImageId"
                    value={formData.cloudinaryImageId}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="row mb-3  align-items-center ">
                <label className="col-form-label col-4 fw-bold">
                  ParentId:
                </label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control bg-light border-radius-10"
                    name="parentId"
                    value={formData.parentId}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="row mb-3  align-items-center ">
                <label className="col-form-label col-4 fw-bold">
                  Rating:
                </label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control bg-light border-radius-10"
                    name="Rating"
                    value={formData.Rating}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="row mb-3  align-items-center ">
                <label className="col-form-label col-4 fw-bold">
                  Delivery Time :
                </label>
                <div className="col-8">
                  <input
                    type="text"
                    className="form-control bg-light border-radius-10"
                    name="DeliveryTime"
                    value={formData.DeliveryTime}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>
              <div className="mb-3">
                <label className="form-label fw-bold mt-4">Availability:</label>
                <div className="form-check form-check-inline">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="availability"
                    id="availabilityTrue"
                    value="true"
                    checked={formData.availability === "true"}
                    onChange={handleChange}
                  />
                  <label
                    className="form-check-label"
                    htmlFor="availabilityTrue"
                  >
                    True
                  </label>
                </div>
                <div className="form-check form-check-inline">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="availability"
                    id="availabilityFalse"
                    value="false"
                    checked={formData.availability === "false"}
                    onChange={handleChange}
                  />
                  <label
                    className="form-check-label"
                    htmlFor="availabilityFalse"
                  >
                    False
                  </label>
                </div>
              </div>
              <div className=" text-center">
                {" "}
                <button type="submit" className="btn btn-primary">
                  Add Restaurant
                </button>
              </div>
              </div>
           
            </form>
          </div>
         
      <div className="col-4 d-flex">
      <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQBK2ByZJcWy5_thhhnLUX8Xqauf7WrYaGGcQ&usqp=CAU" alt="" />
      </div>
        </div>
      </div>
     
       
      
    </>
  );
};

export default AddRestaurantForm;
