// RestaurantDetail.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar from './Navbar';

const RestaurantDetail = () => {
  const { parentId } = useParams();
  const [restaurant, setRestaurant] = useState(null);

  useEffect(() => {
    const fetchRestaurantData = async () => {
      try {
        const response = await fetch(`http://localhost:8001/api/addrestaurant/${parentId}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          const data = await response.json();
          setRestaurant(data);
        } else {
          console.error('Error:', response.status, response.statusText);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchRestaurantData();
  }, [parentId]); // Include parentId in the dependency array

  return (
    <div>
        <Navbar/>
      <div className="container mt-4">
        {restaurant ? (
          <div>
            <h1 className="mt-4">{restaurant.restaurantName}</h1>
            <p>{restaurant.areaName}</p>
            <p>{restaurant.Rating} - {restaurant.availability ? "Open" : "Closed"}</p>
            <p>{restaurant.DeliveryTime} mins - {restaurant.cuisines}</p>
            {/* Add more details you want to display */}
          </div>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default RestaurantDetail;
