import React, { useState, useEffect } from 'react';

const AddItemForm = () => {
  const [formData, setFormData] = useState({
    itemName: 'Pasta Alfredo',
    cloudinaryImageId: 'image3',
    category: 'Pasta',
    parentName: '', // Updated to store the selected restaurant name
    Rating: 5,
    cost: 1,
    availability: true,
    description: 'optional',
  });

  const [parentOptions, setParentOptions] = useState([]);

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await fetch('http://localhost:8001/api/addrestaurant');
       
          
        
       
        

        const data = await response.json();
        console.log(data[0]);
        // Assuming your API response has an array of restaurant objects with a 'name' property
        const restaurantNames = data.map((restaurant) => restaurant.restaurantName);
        
        setParentOptions(restaurantNames);
      } catch (error) {
        console.error('Error fetching restaurant names:', error.message);
      }
    };

    fetchRestaurants();
  }, []);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);

    try {
      const response = await fetch('http://localhost:8001/api/createItem', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log(response);

      if (!response.ok) {
        console.error(`Request failed with status ${response.status}`);
        return;
      }

      const json = await response.json();
      console.log('API Response:', json);

      setFormData({
        itemName: '',
        cloudinaryImageId: '',
        category: '',
        parentName: '',
        Rating: '',
        cost: '',
        availability: '',
        description: '',
      });
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  return (
    <div className="container mt-5">
      <div className="card p-4">
        <h2 className="mb-4">Add a New Item</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="itemName" className="form-label">
              Item Name:
            </label>
            <input
              type="text"
              name="itemName"
              value={formData.itemName}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="cloudinaryImageId" className="form-label">
              Image:
            </label>
            <input
              type="text"
              name="cloudinaryImageId"
              value={formData.cloudinaryImageId}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="cost" className="form-label">
              Cost:
            </label>
            <input
              type="text"
              name="cost"
              value={formData.cost}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="description" className="form-label">
              Description:
            </label>
            <input
              type="text"
              name="description"
              value={formData.description}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="category" className="form-label">
              Category:
            </label>
            <input
              type="text"
              name="category"
              value={formData.category}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="parentName" className="form-label">
              Parent Name (Restaurant):
            </label>
            <select
              name="parentName"
              value={formData.parentName}
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="" disabled>Select a restaurant</option>
              {parentOptions.map((option) => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label htmlFor="Rating" className="form-label">
              Rating:
            </label>
            <input
              type="number"
              name="Rating"
              value={formData.Rating}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="availability" className="form-label">
              Availability:
            </label>
            <input
              type="text"
              name="availability"
              value={formData.availability}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <button type="submit" className="btn btn-primary">
            Add Item
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddItemForm;
