import React, { useState, useEffect } from 'react';

const AddItemForm = () => {
  const [formData, setFormData] = useState({
    itemName: 'Pasta Alfredo',
    cloudinaryImageId: 'image3',
    category: 'Pasta',
    parentName: '', // Updated to store the selected restaurant name
    Rating: 5,
    cost: 1,
    availability: true,
    description: 'optional',
  });

  const [parentOptions, setParentOptions] = useState([]);

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await fetch('http://localhost:8001/api/restaurants');
        if (!response.ok) {
          console.error(`Failed to fetch restaurant names with status ${response.status}`);
          return;
        }

        const data = await response.json();
        // Assuming your API response has an array of restaurant objects with a 'name' property
        const restaurantNames = data.map((restaurant) => restaurant.name);
        setParentOptions(restaurantNames);
      } catch (error) {
        console.error('Error fetching restaurant names:', error.message);
      }
    };

    fetchRestaurants();
  }, []);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'number' ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);

    try {
      const response = await fetch('http://localhost:8001/api/createItem', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      console.log(response);

      if (!response.ok) {
        console.error(`Request failed with status ${response.status}`);
        return;
      }

      const json = await response.json();
      console.log('API Response:', json);

      setFormData({
        itemName: '',
        cloudinaryImageId: '',
        category: '',
        parentName: '',
        Rating: '',
        cost: '',
        availability: '',
        description: '',
      });
    } catch (error) {
      console.error('Error:', error.message);
    }
  };

  return (
    <div className="container mt-5">
      <div className="card p-4">
        <h2 className="mb-4">Add a New Item</h2>
        <form onSubmit={handleSubmit}>
          {/* ... other form fields */}
          <div className="mb-3">
            <label htmlFor="parentName" className="form-label">
              Parent Name (Restaurant):
            </label>
            <select
              name="parentName"
              value={formData.parentName}
              onChange={handleChange}
              className="form-select"
              required
            >
              <option value="" disabled>Select a restaurant</option>
              {parentOptions.map((option) => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
          </div>

          <button type="submit" className="btn btn-primary">
            Add Item
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddItemForm;
